import React from 'react'
import { Typography } from '@ya.praktikum/react-developer-burger-ui-components'
import {Tab} from '@ya.praktikum/react-developer-burger-ui-components'
import ingredientsStyles from './burger-ingredients.module.css'
import Ingredient from '../ingredient/ingredient';



function BurgerIngredients({ingredients, setIsModalOpen}) {

  const onClick = () => {
    setIsModalOpen(true)
  }

  return (
    <section className={ingredientsStyles.section}>

      <h1 className="text text_type_main-large mt-10 mb-5">Соберите бургер</h1>

      <div style={{display: 'flex'}} className='mb-10'>
        <Tab>Булки</Tab>
        <Tab>Соусы</Tab>
        <Tab>Начинки</Tab>
      </div>
      <div className={`${ingredientsStyles.scrollDiv} ${ingredientsStyles.scrollDiv} custom-scroll`}>
        
        <h2 className="text text_type_main-medium ">Булки</h2>
        <ul className={ingredientsStyles.items}>
        {ingredients.map(ingr => (
            ingr.type === 'bun' &&
            <Ingredient
              onClick={onClick}
              name={ingr.name}
              key={ingr._id}
              price={ingr.price}
              image={ingr.image}
            />
        ))}
        </ul>

        <h2 className="text text_type_main-medium">Cоусы</h2>
        <ul className={ingredientsStyles.items}>
        {ingredients.map(ingr => (
            ingr.type === 'sauce' &&
            <Ingredient
              onClick={onClick}
              name={ingr.name}
              key={ingr._id}
              price={ingr.price}
              image={ingr.image}
            />
        ))}
        </ul>

        <h2 className="text text_type_main-medium">Начинки</h2>
        <ul className={ingredientsStyles.items}>
        {ingredients.map(ingr => (
            ingr.type === 'main' &&
            <Ingredient
              onClick={onClick}
              name={ingr.name}
              key={ingr._id}
              price={ingr.price}
              image={ingr.image}
            />
        ))}
        </ul>
      </div>

    </section>
  )
}

export default BurgerIngredients
