import React, { useEffect } from 'react'
import modalStyyles from './modal.module.css'
import ReactDOM from 'react-dom'
import { Typography } from '@ya.praktikum/react-developer-burger-ui-components'
import { CloseIcon } from '@ya.praktikum/react-developer-burger-ui-components'

const modalRoot = document.querySelector('#react-modal')

function Modal({setIsModalOpen, cardData, setCardData}) {

  const onClick = () => {
    setIsModalOpen(false)
    setCardData('null')
  }

  useEffect(() => {
    const closeOnEsc = (evt) => {
      if (evt.code === "Escape") {
        setIsModalOpen(false)
        setCardData('null')
      }
    }

    document.addEventListener('keydown', closeOnEsc)

    return () => document.removeEventListener('keydown', closeOnEsc)
  }, [])


  return ReactDOM.createPortal (
    (    
    <div className={modalStyyles.modal}>
        
      <h2 className={modalStyyles.title + ' text text_type_main-large mt-10 ml-10'}>Детали ингридиента</h2>
       
      <button type='button' className={modalStyyles.closeButton} onClick={onClick}>
        <CloseIcon type="primary" />
      </button>

      {console.log(cardData)}
      {cardData && (
        <>
          <img className={modalStyyles.image} src={cardData.image_large} alt={cardData.name} />

          <p className={'text text_type_main-medium mt-4 mb-8'}>{cardData.name}</p>
    
          <ul className={modalStyyles.caloriesBlock}>
            <li className={modalStyyles.item}>
              <p className='text text_type_main-default text_color_inactive'>Калории,ккал</p>
              <p className="text text_type_digits-default">{cardData.calories}</p>
            </li>
            <li className={modalStyyles.item}>
              <p className='text text_type_main-default text_color_inactive'>Белки, г</p>
              <p className="text text_type_digits-default">{cardData.proteins}</p>
            </li>
            <li className={modalStyyles.item}>
              <p className='text text_type_main-default text_color_inactive'>Жиры, г</p>
              <p className="text text_type_digits-default">{cardData.fat}</p>
            </li>
            <li className={modalStyyles.item}>
              <p className='text text_type_main-default text_color_inactive'>Углеводы, г</p>
              <p className="text text_type_digits-default">{cardData.carbohydrates}</p>
            </li>
          </ul>
        </>
      )}


    </div>
    ), modalRoot
  )
}

export default Modal
