import modalStyyles from './modal.module.css'
import ReactDOM from 'react-dom'

const modalRoot = document.querySelector('#react-modal')

function Modal() {
  return ReactDOM.createPortal (
    (    <div className={modalStyyles.container}>
      <h2>Детали ингридиента</h2>
      <p>asdasdasdasdasdasdsadqadfsdfgsfdgedfh</p>
    </div>
    ), modalRoot
  )
}

export default Modal
