import React, { useEffect } from 'react'
import modalStyyles from './modal.module.css'
import ReactDOM from 'react-dom'
import { Typography } from '@ya.praktikum/react-developer-burger-ui-components'
import { CloseIcon } from '@ya.praktikum/react-developer-burger-ui-components'

const modalRoot = document.querySelector('#react-modal')

function Modal({setIsModalOpen}) {

  const onClick = () => {
    setIsModalOpen(false)
  }

  useEffect(() => {
    const closeOnEsc = (evt) => {
      if (evt.code === "Escape") {
        setIsModalOpen(false)
      }
    }

    document.addEventListener('keydown', closeOnEsc)

    return () => document.removeEventListener('keydown', closeOnEsc)
  }, [])


  return ReactDOM.createPortal (
    (    
    <div className={modalStyyles.modal}>
        
      <h2 className={'text text_type_main-large mt-10 ml-10'}>Детали ингридиента</h2>
      
      <button type='button' className={modalStyyles.closeButton} onClick={onClick}>
        <CloseIcon type="primary" />
      </button>

      <img src="" alt="" />


    </div>
    ), modalRoot
  )
}

export default Modal
