import React, { useEffect } from 'react'
import ReactDOM from 'react-dom'
import IngredientDetails from '../ingredientDetails/ingredientDetails'
import OrderDetails from '../orderDetails/orderDetails'


const modalRoot = document.querySelector('#react-modal')

function Modal({ setIsModalOpen, cardData, currentModal}) {

  const onClick = () => {
    setIsModalOpen(false)
  }
  console.log(currentModal)
  useEffect(() => {
    const closeOnEsc = (evt) => {
      if (evt.code === "Escape") {
        setIsModalOpen(false)
      }
    }

    document.addEventListener('keydown', closeOnEsc)

    return () => document.removeEventListener('keydown', closeOnEsc)
  }, [])


  return ReactDOM.createPortal (
    (    
      // currentModal === 'IngredientDetails' &&
      // <IngredientDetails onClick={onClick} cardData={cardData} />


      <OrderDetails onClick={onClick} cardData={cardData} />

    ), modalRoot
  )
}

export default Modal
